import { createTheme } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    primary: {
      main: "#8093F1",
      contrastText: "#fff",
    },
    secondary: {
      main: "#72DDF7",
    },
  },
  components: {
    MuiTextField: {
      styleOverrides: {
        root: {
          "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline .Mui-focused":
            {
              borderColor: "#560bad",
            },
        },
      },
    },
  },
});

export default theme;
