import {
  Button,
  getAlertTitleUtilityClass,
  TextField,
} from "@material-ui/core";
import { Box } from "@material-ui/system";
import React, { useState } from "react";

function Form() {
  const [state, setState] = useState({
    title: "",
    type: "",
    description: "",
  });

  const handleChange = (event) => {
    const target = event.target;
    const name = target.name;
    const value = target.value;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <Box
      component="form"
      sx={{
        "& .MuiTextField-root": { m: 1, width: "25ch" },
      }}
      noValidate
      autoComplete="off"
      onSubmit={handleSubmit}
    >
      <TextField
        id="outlined-basic"
        name="title"
        label="Title"
        variant="outlined"
        value={state.title}
        onChange={handleChange}
        sx={{ "& .MuiInputBase-root": { border: "2px solid red" } }}
      />
      <Box sx={{ "& .MuiTextField-root": { width: "98%" } }}>
        <TextField
          id="outlined-multiline-flexible"
          name="description"
          label="Write here"
          fullWidth
          multiline
          minRows={10}
          value={state.description}
          onChange={handleChange}
        />
      </Box>
      <TextField
        id="outlined-basic"
        name="type"
        label="Type"
        variant="outlined"
        value={state.type}
        onChange={handleChange}
      />
      <Button
        variant="contained"
        size="large"
        sx={{ width: 150, padding: 1.8, ml: 1, mt: 3 }}
      >
        Add
      </Button>
    </Box>
  );
}

export default Form;
