import { getAlertTitleUtilityClass, TextField } from "@material-ui/core";
import { Box } from "@material-ui/system";
import React, { useState } from "react";

function Form() {
  const [state, setState] = useState({
    title: "",
    description: "",
    type: "",
  });

  const handleChange = (event) => {
    const target = event.target;
    const name = target.name;

    setState({ ...state, [name]: value });
  };

  return (
    <Box
      component="form"
      sx={{
        "& .MuiTextField-root": { m: 1, width: "25ch" },
      }}
      noValidate
      autoComplete="off"
    >
      <TextField
        id="outlined-basic"
        name="title"
        label="Title"
        variant="outlined"
        value={state.title}
        onChange={handleChange}
      />
      <Box sx={{ "& .MuiTextField-root": { width: "98%" } }}>
        <TextField
          id="outlined-multiline-flexible"
          name="description"
          label="Write here"
          fullWidth
          multiline
          minRows={10}
          value={state.description}
          onChange={handleChange}
        />
      </Box>
      <TextField
        id="outlined-basic"
        name="type"
        label="Type"
        variant="outlined"
        value={state.type}
        onChange={handleChange}
      />
    </Box>
  );
}

export default Form;
