import { createTheme } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    primary: {
      main: "#8093F1",
      contrastText: "#fff",
    },
    secondary: {
      main: "#72DDF7",
    },
  },
  components: {
    MuiInputBase: {
      styleOverrides: {
        colorPrimary: {
          "&:hover": { borderColor: "red" },
        },
      },
    },
  },
});

export default theme;
