import {
  Divider,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Toolbar,
} from "@material-ui/core";
import HomeTwoToneIcon from "@mui/icons-material/HomeTwoTone";
import AddCommentTwoToneIcon from "@mui/icons-material/AddCommentTwoTone";
import ArticleTwoToneIcon from "@mui/icons-material/ArticleTwoTone";
import React from "react";

const listItems = [
  {
    text: "Home",
    icon: <HomeTwoToneIcon color="primary" fontSize="large" />,
  },
  {
    text: "New ToDo",
    icon: <AddCommentTwoToneIcon color="primary" fontSize="large" />,
  },
  {
    text: "Todo Details",
    icon: <ArticleTwoToneIcon color="primary" fontSize="large" />,
  },
];

function SideBar({ drawerWidth }) {
  return (
    <>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          "& .MuiDrawer-paper": {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <Divider />
        <List>
          {listItems.map((item, index) => (
            <ListItem button key={index} sx={{ pl: 4 }}>
              <ListItemText sx={{ fontSize: 100 }} primary={item.text} />
              <ListItemIcon>{item.icon}</ListItemIcon>
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
}

export default SideBar;
